CREATE TABLE "Users" (
	"id" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"email" VARCHAR(128) NOT NULL UNIQUE,
	"password" VARBIT(1) NOT NULL,
	"type" VARCHAR(16) NOT NULL DEFAULT 'false',
	"isEmailConfirmed" BOOLEAN NOT NULL DEFAULT false,
	"isSupplierStatusConfirmed" BOOLEAN NOT NULL DEFAULT false,
	"isAdmin" BOOLEAN NOT NULL DEFAULT false,
	PRIMARY KEY("id")
);


CREATE TABLE "CompaniesInfo" (
	"id" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"companyName" VARCHAR(256) NOT NULL UNIQUE,
	"userId" INTEGER NOT NULL,
	PRIMARY KEY("id")
);


CREATE TABLE "ProductAdditionalImages" (
	"id" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"imageUrl" VARCHAR(1024) NOT NULL,
	"productId" INTEGER NOT NULL,
	PRIMARY KEY("id")
);


CREATE TABLE "Catalog" (
	"id" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"title" VARCHAR(128) NOT NULL,
	"titleImage" VARCHAR(1024) NOT NULL,
	"cost" INTEGER NOT NULL,
	"description" TEXT,
	"supplierId" INTEGER NOT NULL,
	"additionalImages" INTEGER ARRAY,
	PRIMARY KEY("id")
);


ALTER TABLE "ProductAdditionalImages"
ADD FOREIGN KEY("productId") REFERENCES "Catalog"("id")
ON UPDATE NO ACTION ON DELETE NO ACTION;
ALTER TABLE "Catalog"
ADD FOREIGN KEY("supplierId") REFERENCES "CompaniesInfo"("id")
ON UPDATE NO ACTION ON DELETE NO ACTION;
ALTER TABLE "CompaniesInfo"
ADD FOREIGN KEY("userId") REFERENCES "Users"("id")
ON UPDATE NO ACTION ON DELETE NO ACTION;
ALTER TABLE "ProductAdditionalImages"
ADD FOREIGN KEY("id") REFERENCES "Catalog"("additionalImages")
ON UPDATE NO ACTION ON DELETE NO ACTION;
